export default class ReportEvents {
    constructor() {
    }
    logMessage(message) {
        process.emit('test:log', message);
        return this;
    }
    ;
    logScreenshot(filename) {
        process.emit('test:screenshot', filename);
        return this;
    }
    ;
    videoCapture(filename) {
        process.emit('test:video-capture', filename);
        return this;
    }
    ;
    connectMessageEvent(fn) {
        process.on('test:log', fn);
        return this;
    }
    ;
    connectScreenshotEvent(fn) {
        process.on('test:screenshot', fn);
        return this;
    }
    ;
    connectVideoCaptureEvent(fn) {
        process.on('test:video-capture', fn);
        return this;
    }
    ;
}
